function Class(aBaseClass, aClassDefine)    //
{
    function class_()   //
    {
        this.Type = aBaseClass;    //Type
        for(var member in aClassDefine)
            this[member] = aClassDefine[member];    //
    };
    class_.prototype = aBaseClass;
    return new class_();
};

function New(aClass, aParams)   //
