Array.prototype.delrep = function(fun) {
    if(!fun) {
        fun = function(d) {return d;};
    }
    var newArr = [];
    this.sort(function(a, b) {
        return fun(a) > fun(b) ? -1 : 1;
    });
    newArr.push(this[0]);
    this.forEach(function(d) {
        if(fun(d) != fun(newArr[0])) {
            newArr.unshift(d);
        }
    });
    return newArr;
};

