var time = 60;
function countDown(which){

  if(time == 0 ){
    $(which).text("");
    $(which).removeAttribute("disabled");
    time = 60;
  }else {
    $(which).text(""+time+"");
    $(which).attr("disabled",true);
    time--;
    setTimeout(function(){
      countDown(which)
    },
    1000)
  }
}
