 * param               {String}        str     
 * return              {String}                
*/
function escapeReg(str) {
        return str.replace(new RegExp("([.*+?^=!:\x24{}()|[\\]\/\\\\])", "g"), "\\\x241");
}  

/**
 * URL Query
 * author:meizz
 * version:2010/12/16
 * param               {String}        url     URL
 * param               {String}        key     Query
 * return              {String}                 query URL
*/

function delUrlQuery(url, key) {
        key = escapeReg(key);
        var reg = new RegExp("((\\?)("+ key +"=[^&]*&)+(?!"+ key +
  "=))|(((\\?|&)"+ key +"=[^&]*)+$)|(&"+ key +"=[^&]*)", "g");
        return url.replace(reg, "\x241")
}  

// 
var str = "http://www.xxx.com/?pn=0";   //  pn=0
delUrlQuery(str, "pn");
